import React, { useState } from 'react';
import { KakaoAPI } from '../api/KakakoAPI.jsx';
import '../Sidebar/Search.scss';
import { CiSearch } from "react-icons/ci";

export const Search = () => {
    const [query, setQuery] = useState('');
    const [results, setResults] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    const handleSearch = async () => {
        if (!query) return;

        setLoading(true);
        setError(null);

        try {
            const items = await KakaoAPI(query);
            setResults(items.documents || []);
            console.log('검색 결과:', items);
        } catch (err) {
            setError('검색 중 에러 발생');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className='search-container'>
            <div className='bar-container'
            >            <div className='search-bar'>
                    <input
                        type='text'
                        value={query}
                        onChange={(e) => setQuery(e.target.value)}
                        placeholder='지역,가게명,지하철역 검색'
                    />
                    <div className='search-i' onClick={handleSearch}>
                        <CiSearch />
                    </div>
                </div>
            </div>
            <div className="sidebar">
                {results.length === 0 && !loading && !error && (
                    <p>검색 결과가 없습니다.</p>
                )}
                {results.map((result, index) => (
                    <div key={index} className="result-item">
                        <h4 className='title'>
                            <a href={result.link} target="_blank" rel="noopener noreferrer">
                                {result.title.replace(/<\/?b>/g, '')}
                            </a>
                        </h4>
                        <p className='category'>{result.category.split('>').pop().replace(/<\/?b>/g, '')}</p>
                        <p className='address'>{result.address.replace(/<\/?b>/g, '')}</p>
                    </div>
                ))}
            </div>
        </div>
    );
};
