import React, { useState } from 'react';
import { KakaoAPI } from '../api/KakakoAPI.jsx';
import '../Sidebar/Search.scss';
import { CiSearch } from "react-icons/ci";

export const Search = () => {
    const [query, setQuery] = useState('');
    const [results, setResults] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    const handleSearch = async () => {
        if (!query) return;

        setLoading(true);
        setError(null);

        try {
            const documents = await KakaoAPI(query); 
            // 음식점만 필터링
            const filteredResults = documents.filter(item => 
                item.category_name && item.category_name.includes('맛집')
            );
            setResults(filteredResults); 
            console.log('검색 결과:', filteredResults);
        } catch (err) {
            setError('검색 중 에러 발생');
            console.error(err); 
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className='search-container'>
            <div className='bar-container'>
                <div className='search-bar'>
                    <input
                        type='text'
                        value={query}
                        onChange={(e) => setQuery(e.target.value)}
                        placeholder='지역, 가게명, 지하철역 검색'
                    />
                    <div className='search-i' onClick={handleSearch}>
                        <CiSearch />
                    </div>
                </div>
            </div>
            <div className="sidebar">
                {loading && <p>로딩 중...</p>} {/* 로딩 중 메시지 추가 */}
                {error && <p>{error}</p>} {/* 오류 메시지 표시 */}
                {results.length === 0 && !loading && !error && (
                    <p>검색 결과가 없습니다.</p>
                )}
                {results.map((result, index) => (
                    <div key={index} className="result-item">
                        <h4 className='title'>
                            <a href={result.place_url} target="_blank" rel="noopener noreferrer">
                                {result.place_name}
                            </a>
                        </h4>
                        <p className='category'>{result.category_name}</p>
                        <p className='address'>{result.road_address_name || result.address_name}</p>
                    </div>
                ))}
            </div>
        </div>
    );
};
