import React, { useState } from 'react';
import { NaverAPI } from '../api/NaverAPI.jsx';
import '../Sidebar/Search.scss';
export const Search = () => {
    const [query, setQuery] = useState('');
    const [results, setResults] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const [page, setPage] = useState(1);

    const handleSearch = async (pageNum = 1) => {
        if (!query) return;

        setLoading(true);
        setError(null);

        try {
            const items = await NaverAPI(query, pageNum,);
            setResults(items);
            console.log('검색 결과:', items);   
        } catch (err) {
            setError('검색 중 에러 발생');
        } finally {
            setLoading(false);
        }
    };

    const hanldPage = async(newPage) => {
        setPage(newPage)
        handleSearch(newPage);
    }

    return (
        <div className='search-container'>
            <div className='search-bar'>
                <input
                    type='text'
                    value={query}
                    onChange={(e) => setQuery(e.target.value)}
                    placeholder='텍스트를 입력하여 주세요'
                />
                <button onClick={handleSearch()}>검색</button>
            </div>
            {loading && <p>로딩 중...</p>}
            {error && <p>{error}</p>}
            <div className="sidebar">
                {results.length === 0 && !loading && !error && (
                    <p>검색 결과가 없습니다.</p>
                )}
                {results.map((result, index) => (
                    <div key={index} className="result-item">
                        <h4>{result.title}</h4>
                        <p>{result.address}</p>
                        <p>{result.roadAddress}</p>
                        <p>{result.telephone}</p>
                        <p>
                            <a href={result.link} target="_blank" rel="noopener noreferrer">
                                자세히 보기
                            </a>
                        </p>
                    </div>
                ))}
            </div>
            <div className="pagination">
                <button onClick={() => hanldPage(page - 1)} disabled={page === 1}>
                    이전
                </button>
                <span>페이지 {page}</span>
                <button onClick={() => hanldPage(page + 1)}>
                    다음
                </button>
            </div>
        </div>
    );
};
