import React, { useState } from 'react';
import { KakaoAPI } from '../api/KakaoAPI.jsx';
import '../Sidebar/Search.scss';
import { CiSearch } from "react-icons/ci";

export const Search = () => {
    const [keyword, setKeyword] = useState('');
    const [restaurants, setRestaurants] = useState([]); 
    const [loading, setLoading] = useState(false); 
    const [error, setError] = useState(null);

    const handleSearch = async () => {
        if (!keyword.trim()) return; 

        setLoading(true);
        setError(null);

        try {
            const results = await KakaoAPI(keyword); 
            setRestaurants(results);
        } catch (err) {
            setError('검색 중 오류가 발생했습니다.'); // 오류 메시지 설정
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className='search-container'>
            <div className='bar-container'>
                <div className='search-bar'>
                    <input
                        type='text'
                        value={keyword}
                        onChange={(e) => setKeyword(e.target.value)} // 입력 변경 처리
                        placeholder='지역, 가게명, 지하철역 검색'
                    />
                    <div className='search-i' onClick={handleSearch}>
                        <CiSearch />
                    </div>
                </div>
            </div>
            <div className="sidebar">
                {loading && <p>로딩 중...</p>} {/* 로딩 중 메시지 */}
                {error && <p>{error}</p>} {/* 오류 메시지 표시 */}
                {!loading && !error && restaurants.length === 0 && (
                    <p>검색 결과가 없습니다.</p>
                )}
                {restaurants.map((restaurant, index) => (
                    <div key={index} className="result-item">
                        <h4 className='title'>
                            <a href={restaurant.place_url} target="_blank" rel="noopener noreferrer">
                                {restaurant.place_name}
                            </a>
                        </h4>
                        <p className='category'>{restaurant.category_name}</p>
                        <p className='address'>{restaurant.road_address_name || restaurant.address_name}</p>
                    </div>
                ))}
            </div>
        </div>
    );
};
